mapper.update:
UPDATE `order`
helper_id = #{helperId}, content = #{content}, help_time = #{helpTime}, phone = #{phone}, reward = #{reward}, status = #{status}, publish_time = #{publishTime}, accept_time = #{acceptTime}, complete_time = #{completeTime},
WHERE order_id = #{orderId}

mapper.select:
SELECT o.*, u1.username as client_username, u1.avatar as client_avatar, u2.username as helper_username, u2.avatar as helper_avatar FROM `order` o LEFT JOIN user u1 ON o.client_id = u1.user_id LEFT JOIN user u2 ON o.helper_id = u2.user_id
o.client_id = #{userId}
o.helper_id = #{userId}
o.status = '0'
(o.client_id = #{userId} OR o.helper_id = #{userId})
AND o.status = #{query.status}
ORDER BY o.publish_time DESC LIMIT #{query.size} OFFSET #{query.size} * (#{query.page} - 1)

SELECT COUNT(*)
FROM `order` o


        o.client_id = #{userId}


        o.helper_id = #{userId}


        o.status = '0'


        (o.client_id = #{userId} OR o.helper_id = #{userId})


        AND o.status = #{query.status}
